    empty:=10;
    full:=0;
    count:=3;
    cobegin
      Procedure Fetch_Water     Procedure Drink_Water
        begin                     begin
        while true                  while true
          p(empty);                   p(full);
          P(count);                   p(count);
          P(mutex1);                  p(mutex2);
            Get Water;                  Get water and
          v(mutex1);                    Drink water;
          P(mutex2);                  p(mutex2);
          pure water into the jar;    v(empty);
          v(mutex2);                  v(count);
          v(count);                 end
          v(full);
        end
    coend
    coend
